import React,  { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import ListGroup from 'react-bootstrap/ListGroup';
import Badge from 'react-bootstrap/Badge';


function App() {

  interface IListItemProps {
    title: string;
    description: string;
    count: number;
  }

  const ListItem = (props: IListItemProps) => (
    <ListGroup.Item as="li"
                    className="d-flex justify-content-between align-items-start">
                      <div className="ms-2 me-auto">
                        <div className="fw-bold">{props.title}</div>
                        {props.description}
                      </div>
                    <Badge bg="primary" pill>
                        {props.count}
                    </Badge>
    </ListGroup.Item>
  );

  interface IUserData {
    data: IUser;
    support: ISupport;
  }
  interface IUser {
    email: string;
    first_name: string;
    id: number;
    last_name: string;
    url_avatar: string;
  }
  interface ISupport {
    url: string;
    text: string;
  }

  const [createdUser, setCreatedUser] = useState<IUser[]>([]);

  async function get() : Promise<IUserData> {
    const response = await fetch('https://reqres.in/api/users/2');
    return await response.json();
  }

  useEffect(() => {

    async function init() {
        const resultGet = await get();
        setCreatedUser([resultGet.data]);
    }

     init();
  }, []);

  return (<>
    {createdUser.map(item => (
            <ListItem key={item.id} title={item.email} description={item.first_name + ' ' + item.last_name} count={item.id}  />
          ))}

  </>);
}

export default App;
