    const initialize = async () => {
      const loadedUser1 = await loadUser("https://reqres.in/api/users/2");
      setUser1(loadedUser1); 
      const loadedUser2 = await loadUser("https://reqres.in/api/users/23");
      setUser2(loadedUser2);
    }
    initialize();
    return () => {

    }
  }, [])


  useEffect(() =>{
    console.log("User1 changed");
  }, [user1])

  useEffect(() =>{
    console.log("User2 changed");
  }, [user2])

  // const handlerButton = () => {
  //   const user = new User();
  //   user.data.first_name = user1?.data.first_name;
  //   setUser1(user);
  //   console.log("Clicked");
  // }


  return (
    <>
    <div className="App">
      <UserComponent user={user1}></UserComponent>
      <Button className='MyBtn' /*onClick={()=> handlerButton()}*/>Click to see response</Button>
    </div>

    <div className="App">
      <UserComponent user={user2}></UserComponent>
      <Button className='MyBtn' /*onClick={()=> handlerButton()}*/>Click</Button>
    </div>
  </>
  );
}


export default App;
