import React,  { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import ListGroup from 'react-bootstrap/ListGroup';
import Badge from 'react-bootstrap/Badge';
import Container from 'react-bootstrap/Container';
import Spinner from 'react-bootstrap/Spinner';
import Card from 'react-bootstrap/Card';

function App() {

  const [loading, setLoading] = useState(true);
  const [header, setHeader] = useState('');

  return ( 
  <Container className="p-3">
  <Container className="pb-1 p-5 mb-4 bg-light rounded-3">
    {loading && <span>Loading...</span>
    }
    {loading 
            ? <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
              </Spinner>
            :
            <>
              <h1 className="header">{header}</h1>
              <List>
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
                <ListItem title="bla bla" description="moo noo loo" count={4}  />
              </List>
            </>
    }

  </Container>
</Container>
);
}
interface IListProps {
  children: React.ReactNode;
}

const List = (props: IListProps) => (
  <ListGroup as="ol" numbered>
    {props.children}
  </ListGroup>
)

  interface IListItemProps {
    title: string;
    description: string;
    count: number;
  }

  const ListItem = (props: IListItemProps) => (
    <ListGroup.Item as="li"
                    className="d-flex justify-content-between align-items-start">
                      <div className="ms-2 me-auto">
                        <div className="fw-bold">{props.title}</div>
                        {props.description}
                      </div>
                    <Badge bg="primary" pill>
                        {props.count}
                    </Badge>
    </ListGroup.Item>
  );


  interface IUserData {
    data: IUser;
    support: ISupport;
  }
  interface IUser {
    email: string;
    first_name: string;
    id: number;
    last_name: string;
    url_avatar: string;
  }
  interface ISupport {
    url: string;
    text: string;
  }

  async function getUser() : Promise<IUserData> {
    const response = await fetch('https://reqres.in/api/users/2');
    return await response.json();
  }
  async function getUserNotFound() : Promise<IUserData> {
    const response = await fetch('https://reqres.in/api/users/23');
    return await response.json();
  }

  export function UserComponent() {

    const [createdUser, setCreatedUser] = React.useState<IUser[]>([]);

    useEffect(() => {

      async function init() {
          const resultGet = await getUser();
          setCreatedUser([resultGet.data]);

      }

       init();
    }, []);

    return(<>
              {createdUser.map(item => (
                      <ListItem key={item.id} title={item.email} description={item.first_name + ' ' + item.last_name} count={item.id}  />
                    ))}
              {createdUser.map(item => (
                      <Card style={{ width: '18rem' }}>
                      <Card.Img variant="top" src="{item.url_avatar}" />
                      <Card.Body>
                        <Card.Title>{item.first_name} {item.last_name}  {item.url_avatar}</Card.Title>
                      </Card.Body>
                    </Card> 
                    ))}

            </>);
  }
export default App;
